ts文件
import { ref, watch } from 'vue';
import type { Ref } from 'vue';

const useAdd = (num1: Ref, num2: Ref) => {
    let addNum = ref(0)
    watch([num1, num2], ([num1, num2]) => {
        addFunc(num1, num2)
    })
    const addFunc = (num1: number, num2: number) => {
        addNum.value = num1 + num2
    }

    return {
        addNum, addFunc
    }
}

export default useAdd

