    },
    // 格式化： 类似于computed 
    getters: {
        getUser(): string {
            let age = this.getAge
            return `名称=${this.user.name},年龄=${age}`
        },
        getAge(): number {
            return this.user.age
        }

    },
    //类似于methods 可以操作异步，同步提交
    actions: {
