```vue title="const refs = ref([]); // 使用回调的方式收集 const setRefs = (el: any) => { refs.value.push(el); }; // 更新时-重置refs onBeforeUpdate(() => { refs.value = []; }); // 关闭 const handleClose = () => refs.value.forEach(v => v.hide());"
const refs = ref([]);
// 使用回调的方式收集
const setRefs = (el: any) => {
  refs.value.push(el);
};
// 更新时-重置refs
onBeforeUpdate(() => {
  refs.value = [];
});
// 关闭
const handleClose = () => refs.value.forEach(v => v.hide());






